const chartData = {
  z: [],
  x: [],
  y: [],
  name: "Location 2",
  hovertemplate: "%{y}, %{x} (%{z} ℃)",
  type: "heatmap",
};

const dates = [];
const times = [];
const temperatures = data.hourly.temperature_2m;

data.hourly.time.forEach((timestamp) => {
  const momentObj = moment(timestamp);
  const date = momentObj.format("DD MMM");
  const time = momentObj.format("HH:00");

  if (!dates.includes(date)) {
    dates.push(date);
  }
  if (!times.includes(time)) {
    times.push(time);
  }
});

const chunkSize = 24;
for (let i = 0; i < temperatures.length; i += chunkSize) {
  const chunk = temperatures.slice(i, i + chunkSize);
  chartData.z.push(chunk);
}

chartData.y = dates;
chartData.x = times;

const heatmapData = { data: [{ ...chartData, type: "heatmap" }] };
const contourData = { data: [{ ...chartData, type: "contour" }] };
const lineChartData = {
  x: data.hourly.time,
  y: data.hourly.temperature_2m,
  type: "scatter",
  hovertemplate: "%{x} (%{y} ℃)",
  name: "Location 2",
};

return { heatmapData, contourData, lineChartData };

